    public void executeGetPostRequest() throws IOException {
        HttpGet request = new HttpGet(BASE + "/comments?postId=1");
        response = client.execute(request);
    }
    public void verifyEmailsAreValid() throws IOException {
        String jsonBody = EntityUtils.toString(response.getEntity());
        List<Posts> messages = new ObjectMapper().readValue(jsonBody, new TypeReference<List<Posts>>() {});
        assertThat(messages.size()).isEqualTo(5);
        assertThat(messages.get(0).getId()).isEqualTo(1);
        assertThat(VALID_EMAIL_REGEX.matcher(messages.get(0).getEmail()).matches()).isTrue();
        assertFalse(messages.stream().map(message -> VALID_EMAIL_REGEX.matcher(message.getEmail()).matches()).anyMatch(result -> !result));


    }

